package org.sd_jwt

import com.nimbusds.jose.jwk.OctetKeyPair
import kotlinx.serialization.SerialName
import kotlinx.serialization.Serializable
import org.junit.jupiter.api.Test
import org.junit.jupiter.api.assertThrows
import kotlin.test.*

internal class SdJwtKtTest {

    @Serializable
    private data class SimpleTestCredential(
        @SerialName("given_name") val givenName: String? = null,
        @SerialName("family_name") val familyName: String? = null,
        val email: String? = null,
        val b: Boolean? = null,
        val age: Int? = null
    )

    private val verifier = "http://verifier.example.com"
    private val issuer = "http://issuer.example.com"

    private val issuerKeyJson =
        """{"kty":"OKP","d":"Pp1foKt6rJAvx0igrBEfOgrT0dgMVQDHmgJZbm2h518","crv":"Ed25519","kid":"IssuerKey","x":"1NYF4EFS2Ov9hqt35fVt2J-dktLV29hs8UFjxbOXnho"}"""
    private val issuerKey = OctetKeyPair.parse(issuerKeyJson)
    private val holderKeyJson =
        """{"kty":"OKP","d":"8G6whDz1owU1k7-TqtP3xEMasdI3t3j2AvpvXVwwrHQ","crv":"Ed25519","kid":"HolderKey","x":"s6gVLINLcCGhGEDTf_v1zMluLZcXj4GOXAfQlOWZM9Q"}"""
    private val holderKey = OctetKeyPair.parse(holderKeyJson)

    private val trustedIssuers = mutableMapOf<String, String>(issuer to issuerKey.toPublicJWK().toJSONString())

    private val nonce = "12345"

    @Test
    fun testSimpleCredential() {
        val testConfig = TestConfig(trustedIssuers, issuerKey, issuer, verifier, nonce, null, "Simple Credential")

        val expectedCredential =
            "eyJraWQiOiJJc3N1ZXJLZXkiLCJhbGciOiJFZERTQSJ9..IkWYy2G79AEFaI7bHQpGuFCxgKPgYJWz5r1PbTAmxX3J7ej8lvI0RfNG76ULu1oGy7ooVJX7B4QzH84b7Gf4Dw."
        val expectedPresentation =
            "eyJraWQiOiJJc3N1ZXJLZXkiLCJhbGciOiJFZERTQSJ9..cVwk7mets4E6I6aQ8waP1acyBkvPtqer7Wz3IrBupocVYY17NAo0rNK33H57Ii9A9ybumC_T_52L_xw2Bd-oBA.eyJhbGciOiJub25lIn0.eyJzZF9yZWxlYXNlIjp7ImdpdmVuX25hbWUiOiJbXCJ6Q1RIMFp0MkE3Y2hWMXNuVGtIdG5nXCIsXCJBbGljZVwiXSIsImVtYWlsIjoiW1wiRTAydnFjd2UxekJ2OWVBc1gzNFlYZ1wiLFwiYWxpY2VAZXhhbXBsZS5jb21cIl0iLCJhZ2UiOiJbXCIzdDdNaWtqUWFsX3l4VnFSelgyNV93XCIsMjFdIn0sImF1ZCI6Imh0dHA6Ly92ZXJpZmllci5leGFtcGxlLmNvbSIsIm5vbmNlIjoiMTIzNDUifQ."
        val claims = SimpleTestCredential("Alice", "Wonderland", "alice@example.com", false, 21)
        val releaseClaims = SimpleTestCredential(givenName = "", email = "", age = 0)
        val expectedClaims = SimpleTestCredential(givenName = "Alice", email = "alice@example.com", age = 21)

        testRoutine(expectedCredential, expectedPresentation, expectedClaims, claims, null, releaseClaims, testConfig)
    }

    @Serializable
    private data class Address(
        @SerialName("street_address") val streetAddress: String? = null,
        val locality: String? = null,
        val region: String? = null,
        val country: String? = null,
        @SerialName("zip_code") val zipCode: Int? = null
    )

    @Serializable
    private data class IdCredential(
        @SerialName("given_name") val givenName: String? = null,
        @SerialName("family_name") val familyName: String? = null,
        val email: String? = null,
        val birthday: String? = null,
        val nicknames: Set<String>? = null,
        val address: Address? = null
    )

    @Test
    fun testAdvancedCredential() {
        val testConfig =
            TestConfig(trustedIssuers, issuerKey, issuer, verifier, nonce, holderKey, "Advanced Credential")

        val expectedCredential =
            "eyJraWQiOiJJc3N1ZXJLZXkiLCJhbGciOiJFZERTQSJ9..hvY39dsHUp2BaubN4mU4oBh6fH-jLhuHmTtGrjt-bQr4v0C8xO1uVRzT6pRkS77DKl1GT0WA69lOhiX7lh8bDw."
        val expectedPresentation =
            "eyJraWQiOiJJc3N1ZXJLZXkiLCJhbGciOiJFZERTQSJ9..hvY39dsHUp2BaubN4mU4oBh6fH-jLhuHmTtGrjt-bQr4v0C8xO1uVRzT6pRkS77DKl1GT0WA69lOhiX7lh8bDw.eyJraWQiOiJIb2xkZXJLZXkiLCJhbGciOiJFZERTQSJ9..-pj_KrrjL4WzSyTA53dIf8wOnd26qBSzwXZzzt9emb7mj1iBK3N7OIB9mXp2K0cHtvvCwohI2ciJDWIuEG8DCA"
        val claims = IdCredential(
            "Alice",
            "Wonderland",
            "alice@example.com",
            "1940-01-01",
            setOf("A", "B"),
            Address("123 Main St", "Anytown", "Anystate", "US", 123456)
        )
        val releaseClaims = IdCredential(givenName = "", familyName = "", nicknames = setOf(), address = Address())
        val expectedClaims = IdCredential(
            givenName = "Alice",
            familyName = "Wonderland",
            nicknames = setOf("A", "B"),
            address = Address("123 Main St", "Anytown", "Anystate", "US", 123456)
        )

        testRoutine(expectedCredential, expectedPresentation, expectedClaims, claims, null, releaseClaims, testConfig)
    }

    @Test
    fun testAdvancedCredentialStructured() {
        val testConfig =
            TestConfig(trustedIssuers, issuerKey, issuer, verifier, nonce, holderKey, "Advanced Credential Structured")
        val expectedCredential = "eyJraWQiOiJJc3N1ZXJLZXkiLCJhbGciOiJFZERTQSJ9..y2xmqi2ayZhCMq2aVEaZgLHlwirHvBfifJmkRVGq2u0qHbsqhrWCvZpAj8meZ5MDNh3JmWlvc4PGTDpJxLWMAA."
        val expectedPresentation = "eyJraWQiOiJJc3N1ZXJLZXkiLCJhbGciOiJFZERTQSJ9..kdtem7EUss9W61aVSOKr2gdpGjY1S5cDjt4QbUDEh9tm9OsPE8jD9ISLNrpYsavQYyCKGCJmdESLcnN3S3xCAg.eyJraWQiOiJIb2xkZXJLZXkiLCJhbGciOiJFZERTQSJ9..c2qf6Q1eHjWE-qK486XjaBZ-IpX6r_6EyhNt3eHhk2p7c1rLHZINmBtxN7yHWhkcaS9m31tF0bpJaXkqRAPMAQ"
        val claims = IdCredential(
            "Alice",
            "Wonderland",
            "alice@example.com",
            "1940-01-01",
            setOf("A", "B"),
            Address("123 Main St", "Anytown", "Anystate", "US", 123456)
        )
        val discloseStructure = IdCredential(address = Address())
        val releaseClaims = IdCredential(
            givenName = "",
            familyName = "",
            nicknames = setOf(),
            address = Address(streetAddress = "", locality = "", zipCode = 0)
        )
        val expectedClaims = IdCredential(
            givenName = "Alice",
            familyName = "Wonderland",
            nicknames = setOf("A", "B"),
            address = Address(streetAddress = "123 Main St", locality = "Anytown", zipCode = 123456)
        )

        testRoutine(
            expectedCredential,
            expectedPresentation,
            expectedClaims,
            claims,
            discloseStructure,
            releaseClaims,
            testConfig
        )
    }

    @Test
    fun noneHeader() {
        // eyJhbGciOiAibm9uZSJ9. => {"alg": "none"}
        val noneHeaderPresentation =
            "eyJhbGciOiAibm9uZSJ9..e5ygxCyVZZe1kt1S1bcK383EYZn3GebiQ0aL9a_hqSFQbxei408OQUnRtYCQFDIxHtE24eY6RteUtAWZTwi0AA.eyJhbGciOiAibm9uZSJ9..POOBnye2FDNE2YLhvmy-knlE1CX3vN2VA5uEebHB4WxEt5PhDCPRJAVh2WG97rfLvA8JCuYZXpD7ACeV-MuKCA"
        assertThrows<Exception> {
            verifyPresentation<IdCredential>(noneHeaderPresentation, trustedIssuers, "12345", verifier)
        }

        val noneHeaderSDJWTRPresentation =
            "eyJraWQiOiJJc3N1ZXJLZXkiLCJhbGciOiJFZERTQSJ9..qUPdx5eU3P_0mMudJ3SNPpckTklUzFyMz5a3mhu59k6NaUNLC22znYprZSX-9rUKXeL5rKudmBCm2LjmL_YwDQ.eyJhbGciOiAibm9uZSJ9..csQ42IpP6Wjk6A0Yobn6HoV7ADfW-5365BKMLbtWQVh3WyEvWMGKXpE3xcB1k3NijekYLcX_oSMKXMIuc8-YCA"
        assertThrows<Exception> {
            verifyPresentation<IdCredential>(noneHeaderSDJWTRPresentation, trustedIssuers, "12345", verifier)
        }
    }
}